import React, { useState } from "react";
import { API_URL } from "../api";

function ItemForm() {
  const [itemCode, setItemCode] = useState("");
  const [name, setName] = useState("");
  const [uom, setUom] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();

    const newItem = { item_code: itemCode, name, uom };

    fetch(`${API_URL}/api/items`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(newItem),
    })
      .then((res) => res.json())
      .then((data) => {
        alert("Item added successfully!");
        setItemCode("");
        setName("");
        setUom("");
      })
      .catch((err) => {
        console.error(err);
        alert("Failed to add item");
      });
  };

  return (
    <div className="container mx-auto p-6 bg-gray-50">
      <div>
        <h2 className="text-2xl font-bold mb-4 text-gray-800">Add New Item</h2>
        <div div className="bg-white rounded-lg shadow-md p-2">
          <ul className="pl-2 ">
            <form onSubmit={handleSubmit}>
              <li className="border-b">
                <div>
                  <label>Item Code:</label>
                  <input
                    type="text"
                    value={itemCode}
                    onChange={(e) => setItemCode(e.target.value)}
                    required
                  />
                </div>
              </li>
              <li className="border-b">
                <div>
                  <label>Item Name:</label>
                  <input
                    type="text"
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                    required
                  />
                </div>
              </li>
              <div>
                <label>UOM:</label>
                <input
                  type="text"
                  value={uom}
                  onChange={(e) => setUom(e.target.value)}
                  required
                />
              </div>
              <button className="p-2 border rounded-md" type="submit">
                Add Item <span>âœ…</span>
              </button>
            </form>
          </ul>
        </div>
      </div>
    </div>
  );
}

export default ItemForm;
