import React, { useEffect, useState } from 'react';
import { API_URL } from '../api';

function StockEntryList() {
  const [entries, setEntries] = useState([]);

  const formatDate = (date) => {
    const formattedDate = new Date(date).toLocaleDateString();
    return formattedDate;
  };

  useEffect(() => {
    fetch(`${API_URL}/api/stock-entries`)
      .then((res) => res.json())
      .then((data) => setEntries(data))
      .catch((error) => console.error('Error fetching stock entries:', error));
  }, []);

  return (
    <div>
      <h2>Stock Entry List</h2>
      <table className="table-auto border-collapse border border-gray-300 w-full">
        <thead className="bg-gray-100">
          <tr>
            <th className="border border-gray-300 p-2">Entry ID</th>
            <th className="border border-gray-300 p-2">Tanggal</th>
            <th className="border border-gray-300 p-2">Type</th>
            <th className="border border-gray-300 p-2">Item Code</th>
            <th className="border border-gray-300 p-2">Batch ID</th>
            <th className="border border-gray-300 p-2">Expiry Date</th>
            <th className="border border-gray-300 p-2">Quantity</th>
          </tr>
        </thead>
        <tbody>
          {entries.map((entry, index) => (
            <tr key={`${entry.entry_id}-${index}`}>
              <td className="border border-gray-300 p-2">{entry.entry_id}</td>
              <td className="border border-gray-300 p-2">{formatDate(entry.tanggal)}</td>
              <td className="border border-gray-300 p-2">{entry.type}</td>
              <td className="border border-gray-300 p-2">{entry.item_code}</td>
              <td className="border border-gray-300 p-2">{entry.batch_id}</td>
              <td className="border border-gray-300 p-2">{formatDate(entry.expiry_date)}</td>
              <td className="border border-gray-300 p-2">{entry.qty}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default StockEntryList;
