import React, { useState } from 'react';

function StockEntryForm() {
  const [entryId, setEntryId] = useState('');
  const [tanggal, setTanggal] = useState('');
  const [type, setType] = useState('');
  const [stockEntryDetails, setStockEntryDetails] = useState([
    { item_code: '', batch_id: '', qty: '' }
  ]);

  const handleDetailChange = (index, field, value) => {
    const newDetails = [...stockEntryDetails];
    newDetails[index][field] = value;
    setStockEntryDetails(newDetails);
  };

  const handleAddDetail = () => {
    setStockEntryDetails([...stockEntryDetails, { item_code: '', batch_id: '', qty: '' }]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const stockEntry = { entry_id: entryId, tanggal, type };
    const data = { stockEntry, stockEntryDetails };
    
    // Implement API call to add stock entry
    try {
      const response = await fetch('http://localhost:5000/api/stock-entries/add', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(data),
      });
      const result = await response.json();
      console.log('Stock Entry Submitted', result);
    } catch (err) {
      console.log('Error:', err);
    }
  };

  return (
    <div>
      <h2>Stock Entry Form</h2>
      <form onSubmit={handleSubmit}>
        <label>Entry ID</label>
        <input
          type="text"
          value={entryId}
          onChange={(e) => setEntryId(e.target.value)}
        />
        <br />
        <label>Tanggal</label>
        <input
          type="date"
          value={tanggal}
          onChange={(e) => setTanggal(e.target.value)}
        />
        <br />
        <label>Type</label>
        <select value={type} onChange={(e) => setType(e.target.value)}>
          <option value="IN">IN</option>
          <option value="OUT">OUT</option>
        </select>
        <br />
        <h3>Stock Entry Details</h3>
        {stockEntryDetails.map((detail, index) => (
          <div key={index}>
            <label>Item Code</label>
            <input
              type="text"
              value={detail.item_code}
              onChange={(e) => handleDetailChange(index, 'item_code', e.target.value)}
            />
            <label>Batch ID</label>
            <input
              type="text"
              value={detail.batch_id}
              onChange={(e) => handleDetailChange(index, 'batch_id', e.target.value)}
            />
            <label>Quantity</label>
            <input
              type="number"
              value={detail.qty}
              onChange={(e) => handleDetailChange(index, 'qty', e.target.value)}
            />
            <br />
          </div>
        ))}
        <button type="button" onClick={handleAddDetail}>Add Detail</button>
        <br />
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default StockEntryForm;
