import React, { useState } from 'react';
import { API_URL } from '../api';

function StockEntryForm() {
  const [entry, setEntry] = useState({
    entry_id: '',
    tanggal: '',
    type: 'IN',
  });
  const [details, setDetails] = useState([
    { item_code: '', expiry_date: '', qty: '' }, // Batch ID dihilangkan untuk tipe IN
  ]);

  // Handle changes for main stock entry
  const handleEntryChange = (e) => {
    const { name, value } = e.target;
    setEntry({ ...entry, [name]: value });
    // Reset details jika tipe diubah
    if (name === 'type') {
      setDetails([
        { item_code: '', expiry_date: '', qty: '' }, // Batch ID dihilangkan
      ]);
    }
  };

  // Handle changes for details
  const handleDetailChange = (index, e) => {
    const { name, value } = e.target;
    const updatedDetails = [...details];
    updatedDetails[index][name] = value;
    setDetails(updatedDetails);
  };

  const addDetailRow = () => {
    const newDetail = entry.type === 'IN' 
      ? { item_code: '', expiry_date: '', qty: '' } // Tanpa Batch ID untuk tipe IN
      : { item_code: '', batch_id: '', expiry_date: '', qty: '' }; // Dengan Batch ID untuk tipe OUT
    setDetails([...details, newDetail]);
  };

  const removeDetailRow = (index) => {
    const updatedDetails = details.filter((_, i) => i !== index);
    setDetails(updatedDetails);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    
    const filteredDetails = details.map((detail) => {
      const { item_code, expiry_date, qty, batch_id } = detail;
      return entry.type === 'IN'
        ? { item_code, expiry_date, qty } // Hilangkan batch_id untuk IN
        : { item_code, batch_id, expiry_date, qty }; // Sertakan batch_id untuk OUT
    });

    const stockEntryData = { ...entry, details: filteredDetails };
    fetch(`${API_URL}/api/stock-entries`, {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify(stockEntryData),
  })
      .then((res) =>  {
        if (res.ok) {
          return res.json();
        }
        throw new Error('Failed to add stock entry');
      })
      
      .then((data) => {
        alert(`Stock Entry ${data.entry_id} added successfully!`);
        setEntry({ entry_id: data.entry_id, tanggal: '', type: 'IN' });
        setDetails([{ item_code: '', expiry_date: '', qty: '' }]);
      })
      
      .catch((error) => {
        console.error(error);
        alert('Error adding Stock Entry!');
      });
  };

  return (
    <div>
      <div className="pb-2">
          <h2 className="text-xl font-semibold text-white">Stock Entry Form</h2>
          <p className="mt-1 text-sm/6 text-gray-300">
            The information entered here will be used across stock management
            processes. Ensure all fields are correctly filled out.
          </p>
        </div>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Entry ID:</label>
          <select
            type="text"
            name="entry_id"
            value={entry.entry_id}
            onChange={handleEntryChange}
          >
            <option value="SE001">SE001</option>
            <option value="SE002">SE002</option>
          </select>
        </div>
        <div>
          <label>Tanggal:</label>
          <input
            type="date"
            name="tanggal"
            value={entry.tanggal}
            onChange={handleEntryChange}
            required
          />
        </div>
        <div>
          <label>Type:</label>
          <select
            name="type"
            value={entry.type}
            onChange={handleEntryChange}
            required
          >
            <option value="IN">IN</option>
            <option value="OUT">OUT</option>
          </select>
        </div>

        <h3>Details</h3>
        {details.map((detail, index) => (
          <div key={index}>
            <label>Item Code:</label>
            <input
              type="text"
              name="item_code"
              value={detail.item_code}
              onChange={(e) => handleDetailChange(index, e)}
              required
            />
            {entry.type === 'OUT' && (
              <>
                <label>Batch ID:</label>
                <input
                  type="text"
                  name="batch_id"
                  value={detail.batch_id || ''}
                  onChange={(e) => handleDetailChange(index, e)}
                  required // Pastikan wajib diisi
                />
              </>
            )}
            <label>Expiry Date:</label>
            <input
              type="date"
              name="expiry_date"
              value={detail.expiry_date}
              onChange={(e) => handleDetailChange(index, e)}
              required
            />
            <label>Quantity:</label>
            <input
              type="number"
              name="qty"
              value={detail.qty}
              onChange={(e) => handleDetailChange(index, e)}
              required
            />
            <button type="button" onClick={() => removeDetailRow(index)}>
              Remove
            </button>
          </div>
        ))}
        <button type="button" onClick={addDetailRow}>
          Add Detail
        </button>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default StockEntryForm;
