import React, { useEffect, useState } from 'react';
import { API_URL } from '../api';

function ItemList() {
  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(true); // Menambahkan state untuk loading

  useEffect(() => {
    // Menarik data dari API
    fetch(`${API_URL}/api/items`)
      .then((res) => res.json())
      .then((data) => {
        setItems(data);
        setLoading(false); // Set loading ke false setelah data berhasil diambil
      })
      .catch((error) => {
        console.error('Error fetching items:', error);
        setLoading(false); // Set loading ke false jika ada error
      });
  }, []); // Empty dependency array berarti useEffect hanya dijalankan sekali saat komponen di-mount

  if (loading) {
    return (
      <div className="flex items-center justify-center h-screen">
        <p className="text-lg font-semibold">Loading...</p>
      </div>
    );
  }

  return (
    <div className="container mx-auto p-6 bg-gray-50">
      <h2 className="text-2xl font-bold mb-4 text-gray-800">Item List</h2>
      {items.length === 0 ? (
        <p>No items available.</p>
      ) : (
        <div className="bg-white rounded-lg shadow-md">
          <table className="w-full border-collapse border border-gray-200 text-left text-sm text-gray-600">
            <thead>
              <tr className="bg-gray-100 text-gray-800">
                <th className="border border-gray-200 p-4">Item Code</th>
                <th className="border border-gray-200 p-4">Item Name</th>
                <th className="border border-gray-200 p-4">UOM</th>
              </tr>
            </thead>
            <tbody>
              {items.map((item) => (
                <tr
                  key={item.item_code}
                  className="hover:bg-gray-50 transition-colors"
                >
                  <td className="border border-gray-200 p-4">{item.item_code}</td>
                  <td className="border border-gray-200 p-4">{item.name}</td>
                  <td className="border border-gray-200 p-4">{item.uom}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}

export default ItemList;
