import React, { useState, useEffect } from 'react';
import { API_URL } from '../api';

function StockLedgerReport() {
  const [stockLedger, setStockLedger] = useState([])
  
  const formatDate = (date) => {
    const formattedDate = new Date(date).toLocaleDateString();
    return formattedDate;
  };;

  useEffect(() => {
    fetch(`${API_URL}/api/stock-ledgers`)
      .then((res) => res.json())
      .then((data) => setStockLedger(data));
  }, []);

  return (
    <div>
      <h2>Stock Ledger Report</h2>
      <table>
        <thead>
          <tr>
            <th>Item Code</th>
            <th>Batch ID</th>
            <th>Tanggal</th>
            <th>Last Stock</th>
            <th>Qty In</th>
            <th>Qty Out</th>
            <th>Current Stock</th>
          </tr>
        </thead>
        <tbody>
          {stockLedger.map((entry) => (
            <tr key={entry.batch_id}>
              <td>{entry.item_code}</td>
              <td>{entry.batch_id}</td>
              <td>{formatDate(entry.tanggal)}</td>
              <td>{entry.last_stock}</td>
              <td>{entry.qty_in}</td>
              <td>{entry.qty_out}</td>
              <td>{entry.current_stock}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default StockLedgerReport;
