import React, { useState, useEffect } from 'react';
import { API_URL } from '../api';

function StockEntryForm() {
  const [entry, setEntry] = useState({
    entry_id: '',
    tanggal: '',
    type: 'IN',
  });
  const [details, setDetails] = useState([
    { item_code: '', batch_id: '', expiry_date: '', qty: '' },
  ]);
  const [stockEntries, setStockEntries] = useState([]);  // State untuk menyimpan daftar stock entries

  // Fetch stock entries ketika komponen di-mount
  useEffect(() => {
    fetch(`${API_URL}/api/stock-entries`)
      .then((res) => res.json())
      .then((data) => setStockEntries(data));
  }, []);  // Kosong array dependency berarti hanya dijalankan sekali saat komponen pertama kali dimuat

  // Handle changes for main stock entry
  const handleEntryChange = (e) => {
    const { name, value } = e.target;
    setEntry({ ...entry, [name]: value });
  };

  // Handle changes for details
  const handleDetailChange = (index, e) => {
    const { name, value } = e.target;
    const updatedDetails = [...details];
    updatedDetails[index][name] = value;
    setDetails(updatedDetails);
  };

  const formatDate = (date) => {
    const formattedDate = new Date(date).toLocaleDateString();
    return formattedDate;
  };


  // Add a new detail row
  const addDetailRow = () => {
    setDetails([...details, { item_code: '', batch_id: '', expiry_date: '', qty: '' }]);
  };

  // Remove a detail row
  const removeDetailRow = (index) => {
    const updatedDetails = details.filter((_, i) => i !== index);
    setDetails(updatedDetails);
  };

  // Submit the form
  const handleSubmit = (e) => {
    e.preventDefault();
    const stockEntryData = { ...entry, details };
    fetch(`${API_URL}/api/stock-entries/add`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(stockEntryData),
    })
    .then((res) => {
      if (res.ok) {
        return res.json();
      }
      throw new Error('Failed to add stock entry');
    })
      .then((data) => {
        alert('Stock Entry added successfully!');
        // Reset form setelah submit
        setEntry({ entry_id: '', tanggal: '', type: 'IN' });
        setDetails([{ item_code: '', batch_id: '', expiry_date: '', qty: '' }]);
        
        // Setelah submit, ambil data stock entries terbaru
        fetch(`${API_URL}/api/stock-entries`)
          .then((res) => res.json())
          .then((data) => setStockEntries(data));  // Perbarui state stock entries
      });
  };

  return (
    <div>
      <h2>Stock Entry Form</h2>
      <form onSubmit={handleSubmit}>
        {/* Main Stock Entry Fields */}
        <div>
          <label>Entry ID:</label>
          <input
            type="text"
            name="entry_id"
            value={entry.entry_id}
            onChange={handleEntryChange}
            required
          />
        </div>
        <div>
          <label>Tanggal:</label>
          <input
            type="date"
            name="tanggal"
            value={entry.tanggal}
            onChange={handleEntryChange}
            required
          />
        </div>
        <div>
          <label>Type:</label>
          <select
            name="type"
            value={entry.type}
            onChange={handleEntryChange}
            required
          >
            <option value="IN">IN</option>
            <option value="OUT">OUT</option>
          </select>
        </div>

        {/* Stock Entry Details */}
        <h3>Details</h3>
        {details.map((detail, index) => (
          <div key={index}>
            <label>Item Code:</label>
            <input
              type="text"
              name="item_code"
              value={detail.item_code}
              onChange={(e) => handleDetailChange(index, e)}
              required
            />
            <label>Batch ID:</label>
            <input
              type="text"
              name="batch_id"
              value={detail.batch_id}
              onChange={(e) => handleDetailChange(index, e)}
              required
            />
            <label>Expiry Date:</label>
            <input
              type="date"
              name="expiry_date"
              value={detail.expiry_date}
              onChange={(e) => handleDetailChange(index, e)}
              required
            />
            <label>Quantity:</label>
            <input
              type="number"
              name="qty"
              value={detail.qty}
              onChange={(e) => handleDetailChange(index, e)}
              required
            />
            <button type="button" onClick={() => removeDetailRow(index)}>
              Remove
            </button>
          </div>
        ))}
        <button type="button" onClick={addDetailRow}>
          Add Detail
        </button>

        {/* Submit Button */}
        <button type="submit">Submit</button>
      </form>

      {/* Tabel untuk menampilkan stock entries */}
      <h3>Stock Entries</h3>
      <table>
        <thead>
          <tr>
            <th>Entry ID</th>
            <th>Tanggal</th>
            <th>Type</th>
          </tr>
        </thead>
        <tbody>
          {stockEntries.map((entry) => (
            <tr key={entry.entry_id}>
              <td>{entry.entry_id}</td>
              <td>{formatDate(entry.tanggal)}</td>
              <td>{entry.type}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default StockEntryForm;
