import React, { useEffect, useState } from 'react';
import { API_URL } from '../api';

function ItemList() {
  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch(`${API_URL}/api/items`)
      .then((res) => res.json())
      .then((data) => {
        setItems(data);
        setLoading(false);
      })
      .catch((error) => {
        console.error('Error fetching items:', error);
        setLoading(false);
      });
  }, []);

  if (loading) {
    return (
      <div className="flex items-center justify-center h-screen">
        <p className="text-lg font-semibold">Loading...</p>
      </div>
    );
  }

  return (
    <div className="container mx-auto p-6 bg-gray-50">
      <h2 className="text-2xl font-bold mb-4 text-gray-800">Activity Income</h2>
      {items.length === 0 ? (
        <p className="text-gray-600">No items available.</p>
      ) : (
        <div className="bg-white rounded-lg shadow-md">
          <ul>
            {items.map((item) => (
              <li
                key={item.item_code}
                className="flex justify-between items-center border-b border-gray-200 p-4 last:border-none"
              >
                {/* PDF Button */}
                <button className="bg-gray-100 hover:bg-gray-200 text-gray-800 font-semibold py-1 px-4 rounded">
                  PDF ↓
                </button>
                {/* Item Details */}
                <div className="flex-1 px-4">
                  <p className="text-gray-800 font-medium">{item.name}</p>
                  <p className="text-sm text-gray-500">{item.item_code}</p>
                </div>
                {/* Item Value */}
                <div className="text-right px-4">
                  <p className="text-gray-800 font-medium">
                    {item.uom === 'PCS' ? '1,380.77 €' : '1,500.00 €'}
                  </p>
                  <p className="text-sm text-gray-500">Invoice value</p>
                </div>
                {/* After Tax */}
                <div className="text-right px-4">
                  <p className="text-gray-800 font-medium">1,247.16 €</p>
                  <p className="text-sm text-gray-500">After tax</p>
                </div>
                {/* Action Buttons */}
                <div className="flex items-center space-x-2">
                  <button className="bg-purple-500 hover:bg-purple-600 text-white font-semibold py-1 px-4 rounded">
                    Fix payment
                  </button>
                  <button className="text-blue-500 hover:text-blue-700">
                    ✏️
                  </button>
                  <button className="text-red-500 hover:text-red-700">
                    ❌
                  </button>
                </div>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default ItemList;
