import React from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import ItemList from './components/ItemList';
import ItemForm from './components/ItemForm';
import StockEntryList from './components/StockEntryList';
import StockEntryForm from './components/StockEntryForm';
import StockLedgerReport from './components/StockLedgerReport';

function App() {
  return (
    <Router>
      <div>
        <h1>Stock Management Dashboard</h1>
        <nav>
          <ul>
            <li><Link to="/items">Item List</Link></li>
            <li><Link to="/add-item">Add Item</Link></li>
            <li><Link to="/stock-entry">Stock Entry List</Link></li>
            <li><Link to="/add-stock-entry">Add Stock Entry</Link></li>
            <li><Link to="/stock-ledger">Stock Ledger Report</Link></li>
          </ul>
        </nav>

        <Routes>
          {/* Item Routes */}
          <Route path="/items" element={<ItemList />} />
          <Route path="/add-item" element={<ItemForm />} />

          {/* Stock Entry Routes */}
          <Route path="/stock-entry" element={<StockEntryList />} />
          <Route path="/add-stock-entry" element={<StockEntryForm />} />

          {/* Stock Ledger Report */}
          <Route path="/stock-ledger" element={<StockLedgerReport />} />

          {/* Redirect to default */}
          <Route path="/" element={<ItemList />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
