import React from 'react';
import './styles.css';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import ItemList from './components/ItemList';
import ItemForm from './components/ItemForm';
import StockEntryList from './components/StockEntryList';
import StockEntryForm from './components/StockEntryForm';
import StockLedgerReport from './components/StockLedgerReport';

function App() {
  return (
    <Router>
      <div className="min-h-screen bg-gray-100">
        {/* Navbar */}
        <nav className="bg-blue-600 text-white p-4">
          <div className="container mx-auto flex justify-between items-center">
            <h1 className="text-lg font-bold">Stock Management Dashboard</h1>
            <ul className="flex space-x-4">
              <li>
                <Link to="/items" className="hover:underline">
                  Item List
                </Link>
              </li>
              <li>
                <Link to="/add-item" className="hover:underline">
                  Add Item
                </Link>
              </li>
              <li>
                <Link to="/stock-entry" className="hover:underline">
                  Stock Entry List
                </Link>
              </li>
              <li>
                <Link to="/add-stock-entry" className="hover:underline">
                  Add Stock Entry
                </Link>
              </li>
              <li>
                <Link to="/stock-ledger" className="hover:underline">
                  Stock Ledger Report
                </Link>
              </li>
            </ul>
          </div>
        </nav>

        {/* Routes */}
        <div className="container mx-auto p-4">
          <Routes>
            {/* Item Routes */}
            <Route path="/items" element={<ItemList />} />
            <Route path="/add-item" element={<ItemForm />} />

            {/* Stock Entry Routes */}
            <Route path="/stock-entry" element={<StockEntryList />} />
            <Route path="/add-stock-entry" element={<StockEntryForm />} />

            {/* Stock Ledger Report */}
            <Route path="/stock-ledger" element={<StockLedgerReport />} />

            {/* Redirect to default */}
            <Route path="/" element={<ItemList />} />
          </Routes>
        </div>
      </div>
    </Router>
  );
}

export default App;
