import React, { useState, useEffect } from "react";
import { API_URL } from "../api";

function StockLedgerReport() {
  const [stockLedger, setStockLedger] = useState([]);

  const formatDate = (date) => {
    const formattedDate = new Date(date).toLocaleDateString();
    return formattedDate;
  };

  useEffect(() => {
    fetch(`${API_URL}/api/stock-ledgers`)
      .then((res) => res.json())
      .then((data) => setStockLedger(data))
      .catch((error) => console.error("Error fetching stock ledger:", error));
  }, []);

  return (
    <div>
      <h2>Stock Ledger Report</h2>
      <table className="table-auto border-collapse border border-gray-300 w-full">
        <thead className="bg-red-100">
          <tr>
            <th className="border border-gray-300 p-2">Tanggal</th>
            <th className="border border-gray-300 p-2">Item Code</th>
            <th className="border border-gray-300 p-2">Item Name</th>
            <th className="border border-gray-300 p-2">Batch ID</th>
            <th className="border border-gray-300 p-2">Last Stock</th>
            <th className="border border-gray-300 p-2">Qty In</th>
            <th className="border border-gray-300 p-2">Qty Out</th>
            <th className="border border-gray-300 p-2">Current Stock</th>
          </tr>
        </thead>
        <tbody>
          {Array.isArray(stockLedger) && stockLedger.length > 0 ? (
            stockLedger.map((entry, index) => (
              <tr key={index}>
                <td className="border border-gray-300 p-2">{formatDate(entry.tanggal)}</td>
                <td className="border border-gray-300 p-2">{entry.item_code}</td>
                <td className="border border-gray-300 p-2">{entry.item_name || "N/A"}</td> {/* Tampilkan nama item */}
                <td className="border border-gray-300 p-2">{entry.batch_id}</td>
                <td className="border border-gray-300 p-2">{entry.last_stock}</td>
                <td className="border border-gray-300 p-2">{entry.qty_in}</td>
                <td className="border border-gray-300 p-2">{entry.qty_out}</td>
                <td className="border border-gray-300 p-2">{entry.current_stock}</td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="8">No data available</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
}

export default StockLedgerReport;
